package huggingface

import (
	"encoding/base64"
	"math/rand"

	"github.com/Logiase/MiraiGo-Template/utils"
)

var logger = utils.GetModuleLogger("service.huggingface")

const blockedImage = ``
const BadPrompt = "fused fingers, messy drawing, ugly, bad feet, bad foot, lowres, bad anatomy, bad hands, text, error, missing fingers, extra digit, fewer digits, cropped, worst quality, low quality, normal quality, jpeg artifacts, signature, watermark, username, blurry"

// IsImageBlocked checks if the image is blocked (filtered by nsfw)
func IsImageBlocked(img []byte) bool {
	b64 := base64.StdEncoding.EncodeToString(img)
	return b64 == blockedImage
}

var mixture = []rune("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789")

func generateSessionHash() string {
	b := make([]rune, 11)
	for i := range b {
		b[i] = mixture[rand.Intn(len(mixture))]
	}
	return string(b)
}
